mapper.select:
SELECT ROOM_ID, ROOM_NAME, ROOM_PRICE, ROOM_DESC, ROOM_IMG_URL
,IF(EXISTS(SELECT * FROM reservation WHERE ROOM_ID = r.ROOM_ID AND USER_CODE = #{userCode}), 'Y', 'N') AS RESERVATION_YN
FROM room r WHERE ROOM_ID = #{roomId}

mapper.insert:
INSERT INTO reservation(
    ROOM_ID, USER_CODE
) VALUES (
    #{roomId}, #{userCode}
)

mapper.delete:
DELETE FROM reservation
WHERE
    ROOM_ID = #{roomId}
    AND USER_CODE = #{userCode}

mapper.insert:
INSERT INTO review(
    ROOM_ID, USER_CODE, REVIEW_SCORE, REVIEW_CONTENT
) VALUES (
    #{roomId}, #{userCode}, #{reviewScore}, #{reviewContent}
)
